/*------------------------------------------------------------------

[TABLE OF CONTENTS]

- GLOBAL
	-- Helpers
- TYPOGRAPHY
- BASIC ELEMENTS
	-- Titles
	-- Buttons
	-- Images
	-- Lists
	-- Forms
		-- Radio/check customisation
	-- Decor elements
	-- Tables
	-- Accordion
	-- Tabs
	-- Effects
	-- Dropcap
	-- Blockquote
	-- Pagination
	-- Alert
	-- Tooltip
	-- Progress bar
	-- Other
- HEADER
- NAVIGATION
- FOOTER
- COMPONENTS
- SLIDERS
	-- Main slider
	-- Other sliders
		-- Type
		-- Modifications
- IE8 page

-------------------------------------------------------------------*/


/* ======= GLOBAL ======= */

select:focus {
	outline-style: none;
}

img.pull-right {
	padding-left: 20px;
}
img.pull-left {
	padding-right: 20px;
}

.layout-theme {
	position: relative;
	top: -1px;
	max-width: 1920px;
	margin: auto;
}

.br {
	display: block;
}
.no-br {
	white-space: nowrap;
}

.helper,
.helper:before {
	display: inline-block;
	vertical-align: middle;
}
.helper:before {
	width: 0;
	height: 100%;
	content: '';
}

.helper-2 {
	display: inline-block;
	width: 0;
	height: 100%;
	vertical-align: middle;
}

.blocks-inline {
	padding-left: 0;
	list-style-type: none;
}
.blocks-inline > li {
	display: inline-block;
	margin-right: -3px;
	vertical-align: top;
}

.table-container {
	width: 100%;
	overflow-y: auto;
	_overflow: auto;
}
.table-container::-webkit-scrollbar {
	width: 14px;
	height: 14px;
	-webkit-appearance: none;
}
.table-container::-webkit-scrollbar-thumb {
	border: 3px solid #fff;
	border-radius: 8px;
	background-color: rgba(0, 0, 0, 0.3);
}


/* ======= TYPOGRAPHY ======= */

.typography-section-other {
	margin-top: -10px;
	padding-bottom: 70px;
}
.typography-title {
	margin-top: 0;
	margin-bottom: 35px;
	font-size: 30px;
	font-weight: 300;
	color: #777;
}
.typography-title-number {
	padding-left: 35px;
	font-size: 16px;
	color: #999;
}
.btn-typography {
	margin-right: 68px;
	margin-bottom: 52px;
	vertical-align: bottom;
}
.typography-table-headings tr td {
	padding-bottom: 24px;
}
.typography-bg {
	display: inline-block;
	margin-bottom: 17px;
}
p:last-child .typography-bg {
	margin-bottom: 0;
}
.typography-blockquote {
	margin-top: 75px;
}
.typography-blockquote + .typography-blockquote {
	margin-top: 53px;
}
.typography-progress-bar {
	margin-bottom: 40px;
}
.table-typography {
	margin-bottom: 74px;
}


/* ======= BASIC ELEMENTS ======= */

/* Titles */

.ui-title-page {
	margin-bottom: 0;
	font: 700 38px 'Titillium Web';
	color: #fff;
	text-transform: uppercase;
}
.ui-subtitle-page {
	margin-bottom: 7px;
	font-size: 20px;
}
.ui-title-block {
	margin-top: 0;
	font-size: 38px;
	font-weight: 700;
	text-align: center;
	text-transform: uppercase;
}
.ui-title-block_mod_color {
	color: #fff;
}
.ui-title-block_mod-a {
	font-weight: 600;
	text-transform: none;
}
.ui-title-block_mod-b {
	font-size: 30px;
	font-weight: 600;
	text-transform: none;
}
.ui-title-block_mod-c {
	font-size: 30px;
	font-weight: 700;
	text-align: left;
}
.ui-title-block_w_bg {
	position: relative;
	margin-bottom: 80px;
	padding-top: 55px;
	padding-bottom: 55px;
	font-weight: 600;
	color: #fff;
}
.ui-title-block_w_bg:before {
	position: absolute;
	top: 0;
	width: 200%;
	height: 100%;
	background-color: #222;
	content: '';
}
.ui-title-block_w_bg-first {
	margin-right: -12px;
	padding-right: 100px;
	text-align: right;
}
.ui-title-block_w_bg-first:before {
	right: 0;
	border-top-right-radius: 10px;
}
.ui-title-block_w_bg-last {
	margin-left: -12px;
	padding-left: 100px;
	text-align: left;
}
.ui-title-block_w_bg-last:before {
	left: 0;
	border-bottom-left-radius: 10px;
}
.ui-title-block_typografy {
	text-align: left;
}
.ui-title-block__inner {
	position: relative;
	z-index: 10;
}
.ui-title-emphasis {
	display: block;
	margin-bottom: 3px;
	font: 300 24px 'Titillium Web';
	color: #333;
	letter-spacing: 0.2em;
}
.ui-title-emphasis_sm {
	font-size: 20px;
}
.ui-subtitle-block {
	font: 400 16px/1.625 'Titillium Web';
	color: #333;
	text-align: center;
	text-transform: uppercase;
}
.ui-subtitle-block_mod-a {
	max-width: 600px;
	margin: 40px auto 57px;
}
.ui-subtitle-block_mod-b {
	margin-top: 43px;
	margin-bottom: 17px;
	font-size: 18px;
	font-weight: 600;
}
.ui-subtitle-block_mod-c {
	max-width: 550px;
	margin: 45px auto 40px;
	font-size: 18px;
}
.ui-title-inner {
	margin-top: 0;
	font: 700 16px 'Titillium Web';
	color: #333;
	text-transform: uppercase;
}
.ui-title-underline {
	font-size: 38px;
	font-weight: 700;
	color: #333;
	text-transform: uppercase;
}


/* Buttons */

.ui-btn {
	display: inline-block;
	margin-bottom: 14px;
	padding: 10px 39px;
	font-size: 14px;
	font-weight: 700;
	border-width: 2px;
	border-style: solid;
	transition: all 0.3s;
	text-transform: uppercase;
}
.ui-btn:hover {
	color: inherit;
	text-decoration: none;
}

.btn_mod-a,
.btn_mod-b,
.btn_mod-c {
	position: relative;
	padding: 18px 88px 18px 35px;
	border-bottom-left-radius: 5px;
	overflow: hidden;
}
.btn_mod-b,
.btn_mod-c {
	border-width: 1px;
	border-style: solid;
}
.btn_mod-a:before,
.btn_mod-b:before,
.btn_mod-c:before {
	position: absolute;
	top: 0;
	right: -30px;
	display: block;
	width: 80px;
	height: 100%;
	background-color: #222;
	content: '';
	transition: all 0.3s;
	transform: skewX(26deg);
}
.btn_mod-a.btn-xs:before,
.btn_mod-b.btn-xs:before,
.btn_mod-c.btn-xs:before {
	width: 63px;
}
.btn_mod-a.btn-sm:before,
.btn_mod-b.btn-sm:before,
.btn_mod-c.btn-sm:before {
	right: -15px;
	width: 54px;
}
.btn_mod-a.btn-lg:before,
.btn_mod-b.btn-lg:before,
.btn_mod-c.btn-lg:before {
	width: 86px;
}
.btn_mod-a:hover {
	color: #fff;
}
.btn_mod-a:after,
.btn_mod-b:after,
.btn_mod-c:after {
	position: absolute;
	top: 24px;
	right: 17px;
	width: 7px;
	height: 7px;
	background-color: #fff;
	z-index: 5;
	content: '';
}
.btn_mod-a.btn-xs:after,
.btn_mod-b.btn-xs:after,
.btn_mod-c.btn-xs:after {
	top: 17px;
	right: 14px;
	width: 4px;
	height: 4px;
}
.btn_mod-a.btn-sm:after,
.btn_mod-b.btn-sm:after,
.btn_mod-c.btn-sm:after {
	top: 20px;
	right: 15px;
	width: 5px;
	height: 5px;
}
.btn_mod-a.btn-lg:after,
.btn_mod-b.btn-lg:after,
.btn_mod-c.btn-lg:after {
	top: 28px;
	right: 22px;
	width: 8px;
	height: 8px;
}
.btn_mod-a.btn-xs,
.btn_mod-b.btn-xs,
.btn_mod-c.btn-xs {
	padding: 9px 62px 9px 25px;
}
.btn_mod-a.btn-sm,
.btn_mod-b.btn-sm,
.btn_mod-c.btn-sm {
	padding: 14px 64px 14px 22px;
}
.btn_mod-a.btn-lg,
.btn_mod-b.btn-lg,
.btn_mod-c.btn-lg {
	padding: 18px 96px 18px 45px;
}
.btn_mod-a {
	color: #fff;
	border: 1px solid #ddd;
	background-color: #222;
}
.btn_mod-b:hover {
	color: #333;
}
.btn_mod-c:hover {
	color: #fff;
}
.btn_mod-b,
.btn_mod-c {
	border-color: #ddd;
	background-color: #fff;
}
.btn__inner {
	position: relative;
	z-index: 1;
}
.btn-primary.btn-xs {
	font-size: 14px;
}

.group-btn-ui .ui-btn:last-child {
	margin-bottom: 0;
}


/* Images */

.img-default {
	margin-bottom: 40px;
}


/* Lists */

.list {
	margin-top: 13px;
	margin-bottom: 23px;
	margin-left: 3px;
}
.list li {
	position: relative;
	margin-bottom: 7px;
	padding-left: 32px;
}
.list li:before {
	position: absolute;
	top: 0;
	left: 0;
}
.list li a {
	color: #777;
}
.list li a:hover {
	text-decoration: none;
}

.list-mark_mod-a li:before {
	font: normal normal normal 16px/1 FontAwesome;
	color: #333;
	content: '\f05d';
}

.list-mark_mod-b li {
	padding-left: 25px;
}
.list-mark_mod-b li:before {
	font: normal normal normal 14px/1 FontAwesome;
	color: #333;
	content: '\f0da';
}

.list-mark_mod-c li:before {
	font: normal normal normal 14px/1 FontAwesome;
	color: #333;
	content: '\f00c';
}
.list-mark_mod-c li:hover:before {
	content: '\f00d';
}

.list-mark_mod-d li {
	padding-left: 25px;
}
.list-mark_mod-d li:before {
	top: 3px;
	width: 5px;
	height: 5px;
	background-color: #333;
	content: '';
}
.list-mark_mod-d li:after {
	position: absolute;
	top: 11px;
	left: 0;
	width: 5px;
	height: 5px;
	content: '';
}

.list-mark_mod-e li:before {
	top: 3px;
	font: normal normal normal 13px/1 FontAwesome;
	content: '\f0a4';
}

.list-num {
	counter-reset: list;
}
.list-num li:before {
	display: inline-block;
	counter-increment: list;
}

.list-num_mod-a li:before {
	font-size: 14px;
	font-weight: 900;
	content: counter(list) '. ';
}

.list-num_mod-b li:before {
	display: block;
	width: 16px;
	height: 16px;
	font-size: 12px;
	color: #fff;
	border-radius: 50%;
	content: counter(list) '';
	text-align: center;
}


/* Forms */

.ui-select {
	width: 100%;
	height: 100%;
	margin-bottom: 0;
	padding: 18px 10px 10px 20px;
	font-size: 12px;
	color: #777;
	border: 1px solid #ddd;
	background-color: #fff;
	letter-spacing: 0.2em;
	text-transform: uppercase;
}
.ui-select__icon {
	position: absolute;
	top: 19px;
	right: 14px;
}
.ui-select__icon:before {
	font: normal normal normal 14px/1 FontAwesome;
	content: '\f107';
}

.select-control {
	margin-bottom: 22px;
}

.ui-form__btn {
	margin-top: 12px;
}
.ui-form__btn.btn-sm {
	height: 48px;
}

.forms__label {
	position: relative;
	display: inline-block;
	margin-bottom: 11px;
	padding-left: 45px;
	cursor: pointer;
}
.forms__label:last-child {
	margin-right: 0;
}
.forms__label-check:before,
.forms__label-radio:before {
	position: absolute;
	left: 0;
	border: 1px solid #fff;
	content: '';
}
.forms__label-check:after,
.forms__label-radio:after {
	position: absolute;
	display: none;
}
.forms__label-check {
	display: block;
	height: 22px;
	margin-top: 0;
	margin-right: 12px;
	margin-bottom: 22px;
	padding-left: 35px;
	font-weight: 400;
}
.forms__label-check:before {
	top: 0;
	width: 22px;
	height: 22px;
	border: 1px solid #ccc;
	box-shadow: inset 1px 1px 4px 0 #cacaca;
}
.forms__label-check:after {
	top: 0;
	left: 0;
	width: 23px;
	height: 22px;
	padding-top: 4px;
	font: normal normal normal 12px/1 FontAwesome;
	color: #fff;
	content: '\f00c';
	text-align: center;
}
.forms__label-check_mod-c:before {
	border-color: #ddd;
	background-color: #fff;
	box-shadow: none;
}
.forms__label-check_mod-c:after {
	top: 6px;
	left: 7px;
	width: 8px;
	height: 8px;
	content: '';
}
.forms__label-radio {
	height: 16px;
	margin-right: 0;
	margin-bottom: 14px;
	padding-left: 23px;
}
.forms__label-radio:before {
	top: 2px;
	width: 16px;
	height: 16px;
	border-radius: 50%;
}
.forms__label-radio:after {
	top: 6px;
	left: 4px;
	width: 8px;
	height: 8px;
	border-radius: 50%;
	content: '';
}
.forms__label-radio_mod-a:after {
	background-color: #fff;
}
.forms__label-radio_mod-b:before {
	border: 1px #ccc solid;
}
.forms__label-radio_mod-c:before {
	border: 1px #ccc solid;
	box-shadow: inset 1px 1px 4px 0 #ccc;
}
.forms__check,
.forms__radio {
	position: absolute;
	display: block;
	width: 0;
	height: 0;
	opacity: 0;
}
.forms__check:checked + .forms__label-check:after,
.forms__radio:checked + .forms__label-radio:after {
	display: block;
}
.forms__check:checked + .forms__label-check:before {
	box-shadow: none;
}
.label-group-inline {
	white-space: nowrap;
}
.forms__label-check_mod-b:after {
	background-color: #333;
}
.forms__label-radio_mod-c:after {
	background-color: #2c2e30;
}
.checkbox-group {
	margin-top: 10px;
	margin-bottom: 50px;
}


/* Decor elements */

.decor-1 {
	width: 200px;
	margin-right: auto;
	margin-left: auto;
	text-align: center;
}
.decor-1:before,
.decor-1:after {
	position: relative;
	top: -6px;
	display: inline-block;
	width: 62px;
	padding-right: 10px;
	padding-left: 10px;
	border-bottom: 1px dotted #000;
	content: '';
}
.decor-1 .icon {
	display: inline-block;
	margin-right: 15px;
	margin-left: 15px;
	font-size: 24px;
}

.decor-2:before,
.decor-2:after {
	display: inline-block;
	width: 12px;
	height: 2px;
	content: '';
	transition: all 0.3s;
}
.decor-2:after {
	margin-left: 3px;
	background-color: #333;
}

.decor-2_mod-a:before,
.decor-2_mod-a:after {
	width: 18px;
	height: 3px;
}
.decor-2_mod-b:before,
.decor-2_mod-b:after {
	width: 10px;
}
.decor-2_mod_white:after {
	background-color: #fff;
}

.decor-3:before,
.decor-3:after {
	display: inline-block;
	width: 5px;
	height: 5px;
	margin-right: 5px;
	content: '';
	transition: all 0.3s;
}
.decor-3:after {
	background-color: #333;
}
.decor-3_mod-a:after {
	background-color: #fff;
}

.decor-4 {
	padding: 5px;
	border-radius: 50%;
	background-color: #f6f6f6;
}
.decor-4 .icon {
	display: block;
	width: 80px;
	height: 80px;
	padding-top: 13px;
	font-size: 50px;
	line-height: 1;
	color: #333;
	border: 1px solid #ddd;
	border-radius: 50%;
	background-color: #fff;
	text-align: center;
}
.decor-4_mod-a {
	border-color: #777;
}
.decor-4_mod-b {
	position: absolute;
	top: 30px;
	right: -45px;
	display: inline-block;
	z-index: 10;
}
.decor-4_mod-c {
	background-color: #fff;
	box-shadow: 0 0 0 2px #777;
}

.ui-title-block + .decor-1 {
	width: 240px;
	margin-top: 17px;
	margin-bottom: 13px;
}
.ui-title-block + .decor-1:before,
.ui-title-block + .decor-1:after {
	width: 90px;
}
.ui-title-block_typografy + .decor-1 {
	margin: 0 0 0 10px;
}
.ui-title-block + .decor-1_mod-a {
	width: 150px;
	margin: 0 0 0;
}
.ui-title-block + .decor-1_mod-a:before,
.ui-title-block + .decor-1_mod-a:after {
	width: 45px;
}
.ui-title-block + .decor-1_mod-b {
	width: 210px;
	margin: 0 0 0;
}
.ui-title-block + .decor-1_mod-b:before,
.ui-title-block + .decor-1_mod-b:after {
	width: 75px;
}

.ui-title-block + .decor-2 {
	margin-top: -10px;
	margin-bottom: 43px;
}

.ui-title-block .decor-4 {
	display: inline-block;
	margin: 0 26px -3px;
	font-weight: normal;
	vertical-align: bottom;
}
.ui-title-block_mod-b .decor-4 {
	vertical-align: middle;
}


/* Tables */

.table_mod-a > thead > tr > th {
	position: relative;
}
.table_mod-a > thead > tr > th:before,
.table_mod-a > thead > tr > th:after {
	position: absolute;
	bottom: 27px;
	width: 10px;
	height: 2px;
	content: '';
}
.table_mod-a > thead > tr > th:before {
	left: 20px;
}
.table_mod-a > thead > tr > th:after {
	left: 33px;
	background-color: #333;
}
.table_mod-b > thead > tr > th {
	background-color: #f6f6f6;
}


/* Accordion */

.accordion {
	margin-bottom: 43px;
}
.accordion .panel-heading {
	position: relative;
}
.accordion .panel-heading .btn-collapse {
	position: absolute;
	top: 10px;
	right: 10px;
	display: block;
	height: 43px;
	padding-left: 5px;
	background-color: #333;
	transition: all 0.3s;
}
.accordion .panel-heading .btn-collapse:before {
	position: absolute;
	left: -10px;
	display: block;
	width: 23px;
	height: 100%;
	content: '';
	transition: all 0.3s;
	transform: skewX(26deg);
}
.accordion .panel .btn-collapse .icon {
	position: relative;
	display: inline-block;
	width: 25px;
	font: normal 700 18px Lato;
	color: #fff;
	vertical-align: middle;
	background-color: transparent;
	z-index: 10;
}
.accordion .panel-heading .collapsed .btn-collapse {
	background-color: transparent;
}
.accordion .btn-collapse .icon:before {
	content: '-';
}
.accordion .btn-collapse.collapsed .icon:before {
	content: '+';
}
.accordion .panel-heading .btn-collapse.collapsed,
.accordion .panel-heading .btn-collapse.collapsed:before {
	background-color: #333;
}
.accordion .panel-heading .btn-collapse + .panel-title + .decor-2 {
	display: block;
}
.accordion .panel-heading .btn-collapse.collapsed + .panel-title + .decor-2 {
	display: none;
}
.accordion .panel-title.panel-passive {
	color: #222;
}
.accordion .panel-default > .panel-heading .panel-title {
	font-weight: 700;
}
.accordion .panel-heading .panel-title {
	font-weight: 400;
}
.accordion + .note + .decor-3 {
	margin-bottom: 15px;
}


/* Tabs */

.tab-content img {
	margin-bottom: 20px;
}


/* Effects */

.btn-effect:hover:before {
	width: 130%;
}

.btn-effect-2 {
	position: relative;
	overflow: hidden;
	z-index: 1;
}
.btn-effect-2:after {
	position: absolute;
	top: 50%;
	left: 50%;
	width: 120%;
	height: 50%;
	opacity: 0;
	z-index: -1;
	content: '';
	transition: all 0.3s ease 0s;
	transform: translateX(-50%) translateY(-50%) rotate(45deg);
}
.btn-effect-2:hover:after {
	height: 500%;
	opacity: 1;
}
.btn-effect-2.btn-primary:after {
	background-color: #fff;
}


/* Dropcap */

.dropcap:first-letter {
	float: left;
	width: 60px;
	height: 60px;
	margin-right: 10px;
	padding: 10px 10px 7px;
	font-size: 46px;
	font-weight: 900;
	line-height: 1;
	color: #fff;
}
.dropcap_mod-b:first-letter {
	background-color: #333;
}


/* Blockquote */

.blockquote cite {
	font-style: normal;
	text-transform: uppercase;
}
.blockquote__autor {
	display: block;
	font: 700 14px 'Titillium Web';
}
.blockquote__company {
	display: block;
	font: 400 10px Lato;
}

.blockquote_mod-a {
	padding: 25px 77px;
	font-size: 16px;
	text-align: center;
}
.blockquote_mod-a:before {
	position: absolute;
	top: -53px;
	left: 50%;
	margin-left: -23px;
	font-family: Merriweather;
	font-size: 72px;
