/* ======= SLIDERS ======= */

/* MAIN SLIDER */

.main-slider {
	color: #fff;
}
.main-slider__text {
	font-size: 20px;
}
.main-slider__title {
	font: 300 2.5vw 'Titillium Web';
}
.main-slider__subtitle {
	font: 700 4vw/1 'Titillium Web';
	text-align: left;
	text-transform: uppercase;
}
.main-slider .sp-thumbnails {
	width: 40px !important;
	height: auto !important;
}
.main-slider .sp-thumbnails-container {
	position: absolute;
	right: 30px;
	left: auto;
	bottom: 555px;
	width: 40px !important;
}
.main-slider .sp-thumbnail-container {
	float: none;
	margin-bottom: 10px;
	cursor: pointer;
}
.main-slider .sp-thumbnail-icon {
	display: block;
	padding-top: 4px;
	font-size: 20px;
	border-bottom-left-radius: 5px;
	background-color: #333;
	box-shadow: 1px 2px 3px 0 rgba(0, 0, 0, 0.1);
	transition: all 0.3s;
	text-align: center;
}
.main-slider .sp-selected-thumbnail .sp-thumbnail-icon,
.main-slider .sp-thumbnail-icon:hover {
	color: #fff;
}


/* OTHER SLIDERS */

.carusel-clients__item {
	display: block;
	height: 100px;
	margin-bottom: 3px;
	border: 1px solid #eee;
	border-top-right-radius: 10px;
	background-color: #fff;
	box-shadow: 0 3px 0 0 #777;
	transition: all 0.3s;
	text-align: center;
}
.carusel-clients__item:hover {
	text-decoration: none;
}
.carusel-clients__img {
	display: inline-block;
	max-width: 90%;
	height: auto;
	vertical-align: middle;
}


.slider-thumbnails {
	position: relative;
}
.slider-thumbnails-main {
	overflow: hidden;
}
.slider-thumbnails-main__item {
	background-color: #222;
}
.slider-thumbnails-main__img {
	float: left;
	width: 50%;
}
.slider-thumbnails-main__info {
	float: right;
	width: 50%;
	padding: 4vw 4vw 2vw 24vw;
	text-align: center;
}
.slider-thumbsnails-main__icon {
	display: block;
	margin-bottom: 1vw;
	font-size: 7vw;
	line-height: 1;
	color: #fff;
}
.slider-thumbsnails-main__text {
	margin-top: 1.8vw;
	font-size: 0.9vw;
	color: #ddd;
}
.slider-thumbsnails-main__text p {
	margin-bottom: 1vw;
}

.slider-thumbnails-nav {
	position: absolute;
	top: 0;
	left: 50%;
	padding-top: 4.3vw;
}
.slider-thumbnails-nav__item {
	position: relative;
	float: none !important;
	width: 21vw !important;
	height: 4.3vw;
	margin-bottom: 2px;
	border-top-right-radius: 5px;
	background-color: rgba(255, 255, 255, 0.1);
	z-index: 10;
	cursor: pointer;
	text-transform: uppercase;
}
.slider-thumbnails-nav__item .decor-3 {
	margin-right: 0.5vw;
}
.slider-thumbnails-nav__text {
	padding: 1.4vw;
	font: 700 1.2vw 'Titillium Web';
	color: #fff;
}
.slider-thumbnails-nav .flex-active-slide .decor-3 {
	display: none;
}


